mapper.select:
select c.*,r.ratio ratio
	from t_cards c
	left join  (
		select cardsId,if((sum(ratio) > 100),0,(100 - sum(ratio))) ratio from t_card 


		and id != #{cardId} 

		and isDel = 0

		group by cardsId 
	) r on c.id = r.cardsId
where isDel = 0;

select a.* from t_cards a left join t_card c on c.cardsId = a.id left join t_user_card u on u.cardId = c.id
group by a.id
limit #{start},#{pageSize}
